func Test_frame_len(t *testing.T) {
	type fields struct {
		isFinal           bool
		sequenceNumber    int
		iV                []byte
		contentLength     int
		encryptedContent  []byte
		authenticationTag []byte
	}
	tests := []struct {
		name   string
		fields fields
		want   int
	}{
		{"finalFrameLen", fields{isFinal: true}, 24},
		{"finalFrameLen", fields{isFinal: true, sequenceNumber: 1}, 24},
		{"finalFrameLen", fields{isFinal: true, sequenceNumber: 2}, 24},
		{"finalFrameLen", fields{isFinal: true, iV: bytes.Repeat([]byte{0x01}, 12)}, 24},
		{"finalFrameLen", fields{isFinal: true, iV: bytes.Repeat([]byte{0x01}, 5)}, 24},
		{"finalFrameLen", fields{isFinal: true, contentLength: 0}, 24},
		{"finalFrameLen", fields{isFinal: true, contentLength: 1024}, 24},
		{"finalFrameLen", fields{isFinal: true, contentLength: 4096}, 24},
		{"finalFrameLen", fields{isFinal: true, encryptedContent: bytes.Repeat([]byte{0x01}, 1023)}, 1047},
		{"finalFrameLen", fields{isFinal: true, encryptedContent: bytes.Repeat([]byte{0x01}, 1024)}, 1048},
		{"finalFrameLen", fields{isFinal: true, encryptedContent: bytes.Repeat([]byte{0x01}, 1025)}, 1049},
		{"finalFrameLen", fields{isFinal: true, authenticationTag: bytes.Repeat([]byte{0x01}, 16)}, 40},
		{"finalFrameLen", fields{isFinal: true, authenticationTag: bytes.Repeat([]byte{0x01}, 12)}, 36},
		{"finalFrameLen", fields{isFinal: true, encryptedContent: bytes.Repeat([]byte{0x01}, 1023), authenticationTag: bytes.Repeat([]byte{0x01}, 16)}, 1063},
		{"finalFrameLen", fields{isFinal: true, encryptedContent: bytes.Repeat([]byte{0x01}, 1024), authenticationTag: bytes.Repeat([]byte{0x01}, 16)}, 1064},
		{"finalFrameLen", fields{isFinal: true, encryptedContent: bytes.Repeat([]byte{0x01}, 1025), authenticationTag: bytes.Repeat([]byte{0x01}, 16)}, 1065},
		{"finalFrameLenComplete", fields{isFinal: true, sequenceNumber: 1, iV: bytes.Repeat([]byte{0x01}, 12), contentLength: 1024, encryptedContent: bytes.Repeat([]byte{0x01}, 1024), authenticationTag: bytes.Repeat([]byte{0x01}, 16)}, 1064},
		{"finalFrameLenComplete", fields{isFinal: true, sequenceNumber: 2, iV: bytes.Repeat([]byte{0x01}, 12), contentLength: 0, encryptedContent: []byte{}, authenticationTag: bytes.Repeat([]byte{0x01}, 16)}, 40},

		{"regularFrameLen", fields{isFinal: false}, 16},
		{"regularFrameLen", fields{isFinal: false, sequenceNumber: 1}, 16},
		{"regularFrameLen", fields{isFinal: false, sequenceNumber: 1}, 16},
		{"regularFrameLen", fields{isFinal: false, iV: bytes.Repeat([]byte{0x01}, 12)}, 16},
		{"regularFrameLen", fields{isFinal: false, iV: bytes.Repeat([]byte{0x01}, 5)}, 16},
		{"regularFrameLen", fields{isFinal: false, contentLength: 0}, 16},
		{"regularFrameLen", fields{isFinal: false, contentLength: 1024}, 16},
		{"regularFrameLen", fields{isFinal: false, contentLength: 4096}, 16},
		{"regularFrameLen", fields{isFinal: false, encryptedContent: bytes.Repeat([]byte{0x01}, 1023)}, 1039},
		{"regularFrameLen", fields{isFinal: false, encryptedContent: bytes.Repeat([]byte{0x01}, 1024)}, 1040},
		{"regularFrameLen", fields{isFinal: false, encryptedContent: bytes.Repeat([]byte{0x01}, 1025)}, 1041},
		{"regularFrameLen", fields{isFinal: false, authenticationTag: bytes.Repeat([]byte{0x01}, 16)}, 32},
		{"regularFrameLen", fields{isFinal: false, authenticationTag: bytes.Repeat([]byte{0x01}, 12)}, 28},
		{"regularFrameLen", fields{isFinal: false, encryptedContent: bytes.Repeat([]byte{0x01}, 1023), authenticationTag: bytes.Repeat([]byte{0x01}, 16)}, 1055},
		{"regularFrameLen", fields{isFinal: false, encryptedContent: bytes.Repeat([]byte{0x01}, 1024), authenticationTag: bytes.Repeat([]byte{0x01}, 16)}, 1056},
		{"regularFrameLen", fields{isFinal: false, encryptedContent: bytes.Repeat([]byte{0x01}, 1025), authenticationTag: bytes.Repeat([]byte{0x01}, 16)}, 1057},
		{"regularFrameLenComplete", fields{isFinal: false, sequenceNumber: 1, iV: bytes.Repeat([]byte{0x01}, 12), contentLength: 1024, encryptedContent: bytes.Repeat([]byte{0x01}, 1024), authenticationTag: bytes.Repeat([]byte{0x01}, 16)}, 1056},
		{"regularFrameLenComplete", fields{isFinal: false, sequenceNumber: 2, iV: bytes.Repeat([]byte{0x01}, 12), contentLength: 0, encryptedContent: []byte{}, authenticationTag: bytes.Repeat([]byte{0x01}, 16)}, 32},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			bf := frame{
				isFinal:           tt.fields.isFinal,
				sequenceNumber:    tt.fields.sequenceNumber,
				iV:                tt.fields.iV,
				contentLength:     tt.fields.contentLength,
				encryptedContent:  tt.fields.encryptedContent,
				authenticationTag: tt.fields.authenticationTag,
			}
			assert.Equalf(t, tt.want, bf.Len(), "Len()")
		})
	}
}

