func Test_messageBody_NewBody(t *testing.T) {
	type args struct {
		algorithmSuite *suite.AlgorithmSuite
		frameLength    int
	}
	tests := []struct {
		name    string
		args    args
		want    *body
		wantErr assert.ErrorAssertionFunc
	}{
		{"nilAlgorithmSuite", args{algorithmSuite: nil, frameLength: 0}, nil, assert.Error},
		{"zeroFrameLength", args{algorithmSuite: suite.AES_256_GCM_HKDF_SHA512_COMMIT_KEY_ECDSA_P384, frameLength: 0}, nil, assert.Error},
		{"body", args{algorithmSuite: suite.AES_256_GCM_HKDF_SHA512_COMMIT_KEY_ECDSA_P384, frameLength: 1024}, &body{algorithmSuite: suite.AES_256_GCM_HKDF_SHA512_COMMIT_KEY_ECDSA_P384, frameLength: 1024, frames: []format.BodyFrame{}, sequenceNumber: 1}, assert.NoError},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			got, err := newBody(tt.args.algorithmSuite, tt.args.frameLength)
			if !tt.wantErr(t, err, fmt.Sprintf("newBody(%v, %v)", tt.args.algorithmSuite, tt.args.frameLength)) {
				return
			}
			assert.Equalf(t, tt.want, got, "newBody(%v, %v)", tt.args.algorithmSuite, tt.args.frameLength)
		})
	}
}

