func buildFrame(f bool, seqNum, iv, contentLen, content, tag int) []byte {
	var seqNumBytes, ivBytes, contentLenBytes, contentBytes, tagBytes []byte
	if seqNum > 0 {
		seqNumBytes = conv.FromInt.Uint32BigEndian(seqNum)
	}
	if iv > 0 {
		ivBytes = make([]byte, iv)
	}
	if contentLen > 0 {
		contentLenBytes = conv.FromInt.Uint32BigEndian(contentLen)
	}
	if content > 0 {
		contentBytes = make([]byte, content)
	}
	if tag > 0 {
		tagBytes = make([]byte, tag)
	}
	if f {
		return concatSlices(finalFrameIndicator, seqNumBytes, ivBytes, contentLenBytes, contentBytes, tagBytes)
	}
	return concatSlices(seqNumBytes, ivBytes, contentBytes, tagBytes)
}

