func Test_body_AddFrame(t *testing.T) {
	type fields struct {
		algorithmSuite *suite.AlgorithmSuite
		frameLength    int
	}
	type args struct {
		final         bool
		seqNum        int
		IV            []byte
		contentLength int
		ciphertext    []byte
		authTag       []byte
	}
	tests := []struct {
		name    string
		wantErr assert.ErrorAssertionFunc
		fields  fields
		args    args
	}{
		{"seq0", assert.Error, fields{suite.AES_256_GCM_HKDF_SHA512_COMMIT_KEY_ECDSA_P384, 16}, args{final: false, seqNum: 0}},
		{"seq2", assert.Error, fields{suite.AES_256_GCM_HKDF_SHA512_COMMIT_KEY_ECDSA_P384, 16}, args{final: false, seqNum: 2}},
		{"seq2", assert.Error, fields{suite.AES_256_GCM_HKDF_SHA512_COMMIT_KEY_ECDSA_P384, 16}, args{final: true, seqNum: 2}},
		{"wrongIV", assert.Error, fields{suite.AES_256_GCM_HKDF_SHA512_COMMIT_KEY_ECDSA_P384, 16}, args{final: true, seqNum: 1, IV: bytes.Repeat([]byte{0x3}, 5)}},
		{"wrongAuthLen", assert.Error, fields{suite.AES_256_GCM_HKDF_SHA512_COMMIT_KEY_ECDSA_P384, 16}, args{final: true, seqNum: 1, IV: bytes.Repeat([]byte{0x3}, 12), authTag: bytes.Repeat([]byte{0x3}, 12)}},
		{"correctAuthLen", assert.NoError, fields{suite.AES_256_GCM_HKDF_SHA512_COMMIT_KEY_ECDSA_P384, 16}, args{final: true, seqNum: 1, IV: bytes.Repeat([]byte{0x3}, 12), authTag: bytes.Repeat([]byte{0x3}, 16)}},
		{"wrongContentLen", assert.Error, fields{suite.AES_256_GCM_HKDF_SHA512_COMMIT_KEY_ECDSA_P384, 16}, args{final: true, seqNum: 1, IV: bytes.Repeat([]byte{0x3}, 12), authTag: bytes.Repeat([]byte{0x3}, 16), contentLength: 1}},
		{"wrongContentLen", assert.Error, fields{suite.AES_256_GCM_HKDF_SHA512_COMMIT_KEY_ECDSA_P384, 16}, args{final: true, seqNum: 1, IV: bytes.Repeat([]byte{0x3}, 12), authTag: bytes.Repeat([]byte{0x3}, 16), ciphertext: []byte{0x15}}},
		{"wrongContentLen", assert.Error, fields{suite.AES_256_GCM_HKDF_SHA512_COMMIT_KEY_ECDSA_P384, 16}, args{final: true, seqNum: 1, IV: bytes.Repeat([]byte{0x3}, 12), authTag: bytes.Repeat([]byte{0x3}, 16), contentLength: 0, ciphertext: []byte{0x10, 0x15}}},
		{"correctArgs", assert.NoError, fields{suite.AES_256_GCM_HKDF_SHA512_COMMIT_KEY_ECDSA_P384, 16}, args{final: true, seqNum: 1, IV: bytes.Repeat([]byte{0x3}, 12), authTag: bytes.Repeat([]byte{0x3}, 16), contentLength: 2, ciphertext: []byte{0x10, 0x15}}},
		{"correctArgs", assert.NoError, fields{suite.AES_256_GCM_HKDF_SHA512_COMMIT_KEY_ECDSA_P384, 16}, args{final: true, seqNum: 1, IV: bytes.Repeat([]byte{0x3}, 12), authTag: bytes.Repeat([]byte{0x3}, 16), contentLength: 0, ciphertext: []byte{}}},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			b, err := newBody(tt.fields.algorithmSuite, tt.fields.frameLength)
			require.NoError(t, err)
			tt.wantErr(t, b.AddFrame(tt.args.final, tt.args.seqNum, tt.args.IV, tt.args.contentLength, tt.args.ciphertext, tt.args.authTag), fmt.Sprintf("AddFrame(%v, %v, %v, %v, %v, %v)", tt.args.final, tt.args.seqNum, tt.args.IV, tt.args.contentLength, tt.args.ciphertext, tt.args.authTag))
		})
	}
}

func TestFinalFrameIndicator(t *testing.T) {
	tests := []struct {
		name       string
		assertFunc assert.ComparisonAssertionFunc
		arg        []byte
		want       []byte
	}{
		{"empty", assert.NotEqual, []byte{}, finalFrameIndicator},
		{"nil", assert.NotEqual, nil, finalFrameIndicator},
		{"wrong", assert.NotEqual, []byte{0x0, 0x0, 0x0, 0x1}, finalFrameIndicator},
		{"wrong", assert.NotEqual, []byte{0x0f, 0x0f, 0x0f, 0x0f}, finalFrameIndicator},
		{"correct", assert.Equal, []byte{0xff, 0xff, 0xff, 0xff}, finalFrameIndicator},
		{"correct", assert.EqualValues, []byte{0xff, 0xff, 0xff, 0xff}, finalFrameIndicator},
		{"wrongLen", assert.NotSame, []byte{0xff, 0xff, 0xff}, finalFrameIndicator},
		{"wrongLen", assert.NotSame, []byte{0xff, 0xff, 0xff, 0xff, 0xff}, finalFrameIndicator},
	}
	for _, test := range tests {
		t.Run(test.name, func(t *testing.T) {
			test.assertFunc(t, test.arg, test.want)
		})
	}
}
